import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

const fetchPortfolioData = () => async (dispatch) => {
  try {
    // dispatch(setLoading());
    const response = await fetch("/api/portfolio");
    const data = await response.json();
    // if (response.ok) {
    //   dispatch(setPortfolioData(data));
    // } else {
    //   dispatch(setError(data.message));
    // }
    // dispatch(unsetLoading());
  } catch (error) {
    // dispatch(setError(error.message));
    // dispatch(unsetLoading());
  }
};

const AdminProjects = () => {
  const dispatch = useDispatch();
  const { portfolioData, loading, error } = useSelector((state) => state.root);
  const projects = portfolioData && portfolioData.projects;

  useEffect(() => {
    dispatch(fetchPortfolioData());
  }, [dispatch]);

  return (
    <div>
      <p>Admin Projects</p>
      <div className="adminProject-div">
        {loading && <p>Loading...</p>}
        {error && <p>Error: {error}</p>}
        {projects &&
          projects.map((project) => (
            <div key={project._id}>
              <h3>Project Title : {project.title}</h3>
              <img src={project.imageSrc} alt={project.title} />
              <p>Description : {project.description} </p>
            </div>
          ))}
      </div>
    </div>
  );
};

export default AdminProjects;
