import { Form, Input } from "antd";
import React from "react";
import { useSelector } from "react-redux";

const AdminSkills = () => {
  const { portfolioData } = useSelector((state) => state.root);
  //   const skills = portfolioData ? portfolioData.skills : [];
  const initialValues =
    portfolioData && portfolioData.skills.length > 0
      ? {
          title: portfolioData.skills[0].title,
          imageSrc: portfolioData.skills[0].imageSrc,
        }
      : { title: "", imageSrc: "" };
  const onFinish = (values) => {
    console.log(values);
  };
  return (
    <div>
      <p>Admin Skills</p>
      <Form onFinish={onFinish} layout="vertical" initialValues={initialValues}>
        <Form.Item name="title" label="Titile">
          <Input placeholder="title" />
        </Form.Item>
        <Form.Item name="imageSrc" label="Upload Image">
          <Input type="file" />
        </Form.Item>
        <div className=" skills-btn">
          <button className="btn btn-warning">SAVE</button>
        </div>
      </Form>
    </div>
  );
};

export default AdminSkills;
