import { Form, Input, message } from "antd";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { HideLoading, ShowLoading } from "../redux/rootSlice";

const AdminSkills = () => {
  const dispatch = useDispatch();
  const { portfolioData } = useSelector((state) => state.root);

  // Check if portfolioData exists and has skills
  const initialValues =
    portfolioData && portfolioData.skills && portfolioData.skills.length > 0
      ? {
          title: portfolioData.skills[0].title,
          imageSrc: portfolioData.skills[0].imageSrc,
        }
      : { title: "", imageSrc: "" };

  const onFinish = async (values) => {
    try {
      dispatch(ShowLoading());
      const response = await fetch("/api/portfolio/update-skills", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          ...values,
          _id: portfolioData.skills[0]._id, // Assuming you're updating the first skill
        }),
      });
      dispatch(HideLoading());
      const data = await response.json();
      if (response.ok) {
        message.success(data.message);
      } else {
        message.error(data.message);
      }
    } catch (error) {
      dispatch(HideLoading());
      message.error(error.message);
    }
  };

  return (
    <div>
      <p>Admin Skills</p>
      <Form onFinish={onFinish} layout="vertical" initialValues={initialValues}>
        <Form.Item name="title" label="Title">
          <Input placeholder="Title" />
        </Form.Item>
        <Form.Item name="imageSrc" label="Upload Image">
          <Input type="file" />
        </Form.Item>
        <div className="skills-btn">
          <button className="btn btn-warning" type="submit">
            SAVE
          </button>
        </div>
      </Form>
    </div>
  );
};

export default AdminSkills;
