import { Form, Input, message } from "antd";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { HideLoading, ShowLoading } from "../redux/rootSlice";

const AdminSkills = () => {
  const dispatch = useDispatch();
  const { portfolioData } = useSelector((state) => state.root);
  const skills = portfolioData ? portfolioData.skills : [];
  const initialValues =
    portfolioData && portfolioData.skills.length > 0
      ? {
          title: portfolioData.skills[0].title,
          imageSrc: portfolioData.skills[0].imageSrc,
        }
      : { title: "", imageSrc: "" };
  const onFinish = async (values) => {
    try {
      dispatch(ShowLoading());
      const response = await fetch("/api/portfolio/update-skills", {
        ...values,
        _id: portfolioData.skills._id,
      });
      dispatch(HideLoading());
      if (response.formData.success) {
        message.success(response.data.message);
      } else {
        message.error(response.data.message);
      }
    } catch (error) {
      dispatch(HideLoading());
      message.error(error.message);
    }
  };
  return (
    <div>
      <p>Admin Skills</p>
      <Form
        onFinish={onFinish}
        layout="vertical"
        initialValues={portfolioData.skills}
      >
        <Form.Item name="title" label="Titile">
          <Input placeholder="title" />
        </Form.Item>
        <Form.Item name="imageSrc" label="Upload Image">
          <Input type="file" />
        </Form.Item>
        <div className=" skills-btn">
          <button className="btn btn-warning" type="submit">
            SAVE
          </button>
        </div>
      </Form>
    </div>
  );
};

export default AdminSkills;
